mapper.insert:
INSERT book (userId, bookId, title, splash, img, openType, content, description, bookType) VALUES (#{userId}, #{bookId}, #{title}, #{splash}, #{img}, #{openType}, #{content}
,#{description}
,#{bookType} )

mapper.delete:
DELETE
FROM book
WHERE userId = #{userId}
  AND bookId = #{bookId}

mapper.select:
SELECT *
FROM book
WHERE

    userId = #{userId}
    AND

bookId = #{bookId}
